import axios from 'axios';
import { Link } from "react-router-dom";
import React, { Component } from "react";

export const SERVER_URL = "http://localhost:8080";
const url = `${SERVER_URL}/api/auth/crear-modelo`;


const getAll = () => {
    return axios.get(url);
  };


export default class TutorialsList extends Component {
  constructor(props) {
    super(props);
    this.retrieveModelos = this.retrieveModelos.bind(this);
    this.refreshList = this.refreshList.bind(this);
    this.setActiveModelo = this.setActiveModelos.bind(this);

    this.state = {
      modelos: [],
      currentTutorial: null,
      currentIndex: -1,
    };
  }

  componentDidMount() {
    this.retrieveModelos();
  }

  retrieveTutorials() {
    getAll()
      .then(response => {
        this.setState({
          mdelos: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  refreshList() {
    this.retrieveModelos();
    this.setState({
        currentModelo: null,
        currentIndex: -1
    });
  }

  setActiveModelo(modelo, index) {
    this.setState({
      currentModelo: modelo,
      currentIndex: index
    });
  }

  render() {
    const { modelos, currentModelo, currentIndex } = this.state;

    return (
      <div className="list row">
        <div className="col-md-8">
          <div className="input-group mb-3">
            <div className="input-group-append">
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <h4>Modelos List</h4>

          <ul className="list-group">
            {modelos &&
              modelos.map((modelo, index) => (
                <li
                  className={
                    "list-group-item " +
                    (index === currentIndex ? "active" : "")
                  }
                  onClick={() => this.setActiveModelo(modelos, index)}
                  key={index}
                >
                  {modelos.title}
                </li>
              ))}
          </ul>

        </div>
        <div className="col-md-6">
          {currentModelo ? (
            <div>
              <h4>Modelo</h4>
              <div>
                <label>
                  <strong>Title:</strong>
                </label>{" "}
                {currentModelo.title}
              </div>
              <div>
                <label>
                  <strong>Description:</strong>
                </label>{" "}
                {currentModelo.description}
              </div>
              <div>
                <label>
                  <strong>Status:</strong>
                </label>{" "}
                {currentModelo.published ? "Published" : "Pending"}
              </div>

              <Link
                to={"/tutorials/" + currentModelo.id}
                className="badge badge-warning"
              >
                Edit
              </Link>
            </div>
          ) : (
            <div>
              <br />
              <p>Please click on a Modelo...</p>
            </div>
          )}
        </div>
      </div>
    );
  }
}
