import axios from 'axios';
import { Link } from "react-router-dom";
import React, { Component } from "react";

export const SERVER_URL = "http://localhost:8080";
const url = `${SERVER_URL}/api/auth/crear-modelo`;


const getAll = () => {
    return axios.get(url);
  };


export default class ModelosList extends Component {
  constructor(props) {
    super(props);
    this.retrieveModelos = this.retrieveModelos.bind(this);
    this.refreshList = this.refreshList.bind(this);
    this.setActiveModelo = this.setActiveModelos.bind(this);

    this.state = {
      modelos: [],
      currentModelo: null,
      currentIndex: -1,
    };
  }

  componentDidMount() {
    this.retrieveModelos();
  }

  retrieveModelos() {
    getAll()
      .then(response => {
        this.setState({
          modelos: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  refreshList() {
    this.retrieveModelos();
    this.setState({
        currentModelo: null,
        currentIndex: -1
    });
  }

  setActiveModelo(modelo, index) {
    this.setState({
      currentModelo: modelo,
      currentIndex: index
    });
  }

  render() {
    return (
        <div className="list row">
          <div className="col-md-8">
            <div className="input-group mb-3">
              <input
                type="text"
                className="form-control"
                placeholder="Search by title"
                value={searchTitle}
                onChange={this.onChangeSearchTitle}
              />
              <div className="input-group-append">
                <button
                  className="btn btn-outline-secondary"
                  type="button"
                  onClick={this.searchTitle}
                >
                  Search
                </button>
              </div>
            </div>
          </div>
          <div className="col-md-6">
            <h4>Tutorials List</h4>

            <ul className="list-group">
              {modelos &&
                tutorials.map((tutorial, index) => (
                  <li
                    className={
                      "list-group-item " +
                      (index === currentIndex ? "active" : "")
                    }
                    onClick={() => this.setActiveTutorial(modelo, index)}
                    key={index}
                  >
                    {modelo.title}
                  </li>
                ))}
            </ul>

            <button
              className="m-3 btn btn-sm btn-danger"
              onClick={this.removeAllTutorials}
            >
              Remove All
            </button>
          </div>
          <div className="col-md-6">
            {currentModelo ? (
              <div>
                <h4>Modelo</h4>
                <div>
                  <label>
                    <strong>Title:</strong>
                  </label>{" "}
                  {currentModelo.title}
                </div>
                <div>
                  <label>
                    <strong>Description:</strong>
                  </label>{" "}
                  {currentModelo.description}
                </div>
                <div>
                  <label>
                    <strong>Status:</strong>
                  </label>{" "}
                  {currentModelo.published ? "Published" : "Pending"}
                </div>

                <Link
                  to={"/tutorials/" + currentModelo.id}
                  className="badge badge-warning"
                >
                  Edit
                </Link>
              </div>
            ) : (
              <div>
                <br />
                <p>Please click on a Modelos...</p>
              </div>
            )}
          </div>
        </div>
      );
    }
  }
